mapper.select:
select user_id as id
  from userstatus
 where user_id = #{id}

select user_id as id
from userstatus
where kakao_id = #{id}

select user_id as id,
       user_nm
from userstatus
where user_id = #{id}
  and user_password = #{password}

mapper.update:
CALL schedule_all_delete(
#{work_date},
#{parent_id},
#{output_val, mode=OUT, jdbcType=VARCHAR} ,
#{output_msg, mode=OUT, jdbcType=VARCHAR}
)

CALL kakao_user_mod(
#{kakaoId},
#{output_val, mode=OUT, jdbcType=VARCHAR} ,
#{output_msg, mode=OUT, jdbcType=VARCHAR}
)
